import React from 'react'

export function Offer () {
  const [state, setState] = React.useState({
    order_id: "",
    offered_price: "",
    delivery_date: ""
  })

  function handleChange(evt) {
    const value = evt.target.value;
    setState({
      ...state,
      [evt.target.name]: value
    });
  }
  return (
    <form>
      <label>
        Order ID
        <input
          type="text"
          value={state.order_id}
          onChange={handleChange}
        />
      </label>
      <label>
        Offer price
        <input
          type="text"
          value={state.offered_price}
          onChange={handleChange}
        />
      </label>
      <label>
        Suggest date
        <input
          type="text"
          value={state.delivery_date}
          onChange={handleChange}
        />
      </label>
    </form>
  );
}
