import React, { useEffect, useState } from 'react'
import axios from 'axios';

export const Order = () => {
    const [ord, getOrders] = useState('');

    //Loads data when  the page is loaded
    useEffect(()=> {
        getAllOrders();
    }, []);

    //sends a @GET request to API
    const getAllOrders = () => {
    axios.get('https://pasd-webshop-api.onrender.com/api/order/', {
        headers: {
            'accept': 'application/json',
            'x-api-key': '4sqmKzToVkUoTsVfze4X'
        }
    }).then(res => {
        const allOrders = res.data;
        getOrders(allOrders);
    });
    }


    function Order(props) {
        console.log(props.orders)
        const orders = props.orders;
        if(orders.length > 0) {
            return(
                orders.map((order, index) => {
                    console.log(order);
                    return(
                        <div>
                            <h1>{order.id}</h1>
                            <h2>{order.is_perishable}</h2>
                            <h3>{order.is_breakable}</h3>
                        </div>
                    )
                }) 
            )
        } else {
            return <h3>No orders yet</h3>
        }
    }

    return (
       <Order orders = {ord} />
    )
}
