import React, {useState} from 'react'
import MDBInput from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export function Offer () {
  const [order_id, setOrderId] = useState("");
  const [price_in_cents, setPrice] = useState("");
  const [expected_delivery_datetime, setDate] = useState("");

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://pasd-webshop-api.onrender.com/api/delivery/", {
        headers: {
          'accept': 'application/json',
          'x-api-key': '4sqmKzToVkUoTsVfze4X',
          'Content-Type': 'application/json'
        },
        method: "POST",
        body: JSON.stringify({
          order_id: order_id,
          price_in_cents: price_in_cents,
          expected_delivery_datetime: expected_delivery_datetime,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setOrderId("");
        setPrice("");
        setDate("");
        alert("Offer created successfully");
      } else {
        alert("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  }

  return (
    <form onSubmit={handleSubmit} className = "mx-auto">
      <div class="mb-3">
        <label for="order_id" class="form-label">Order ID</label>
          <input
            type="text"
            defaultVdsalue={state.order_id}
            onChange={(e) => setOrderId(e.target.value)}
            class="form-control"
          />
      </div>
      <div class="mb-3">
        <label class="form-label">
          Offer price in cents
          <input
            type="text"
            defaultValue={state.offered_price}
            onChange={(e) => setPrice(e.target.value)}
            class="form-control"
          />
        </label>
      </div>
      <div class="mb-3">
        <label class="form-label">
          Suggest date
          <input
            type="datetime-local"
            defaultValue={state.delivery_date}
            onChange={(e) => setDate(e.target.value)}
            class="form-control"
          />
        </label>
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
    </form>
  );
}
