import React, { useEffect, useState } from 'react'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


export const Order = () => {
    const [ord, getOrders] = useState('');

    //Loads data when  the page is loaded
    useEffect(()=> {
        getAllOrders();
    }, []);

    //sends a @GET request to API
    const getAllOrders = () => {
    axios.get('https://pasd-webshop-api.onrender.com/api/order/', {
        headers: {
            'accept': 'application/json',
            'x-api-key': '4sqmKzToVkUoTsVfze4X'
        }
    }).then(res => {
        const allOrders = res.data.orders;
        getOrders(allOrders);
    });
    }


    function Order(props) {
        //console.log(props.orders)
        const {menu, orders} = props;
        //console.log(orders.length);
        if(orders.length > 0) {
            return(
                orders.map((order, index) => {
                    console.log(order);
                    return ( 
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>Id: {order.id}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Date: {order.send_date}</Card.Subtitle>
                                <Card.Text>
                                x: {order.x_in_mm} y: {order.y_in_mm} 
                                </Card.Text>
                                <Card.Text>
                                Sender: {order.sender_info.street_and_number} {order.sender_info.zipcode}
                                </Card.Text>
                                <Card.Text>
                                Receiver: {order.receiver_info.street_and_number} {order.sender_info.zipcode}
                                </Card.Text>
                                <Card.Link href="#">Post an offer</Card.Link>
                                <Card.Link href="#">Check on the map</Card.Link>
                            </Card.Body>
                        </Card> 
                        /* <div key = {order.id}>
                            <h1>Id: {order.id}</h1>
                            <h5>Date: {order.send_date}</h5>
                            <h5>X: {order.x_in_mm}</h5>
                            <h5>Y: {order.y_in_mm}</h5>
                            <h5>Receiver: {order.receiver_info.name}</h5>
                            <h5>Sender: {order.sender_info.name}</h5>
                        </div> */
                    )
                })
            )
        } else {
            return <h3>No orders yet.</h3>
        }
    }

    return (
       <Order orders = {ord} />
    )
}
