import React from 'react'
import MDBInput from 'react-bootstrap/Form';

export function Offer () {
  const [state, setState] = React.useState({
    order_id: "",
    offered_price: "",
    delivery_date: ""
  })

  function handleChange(evt) {
    const value = evt.target.value;
    setState({
      ...state,
      [evt.target.name]: value
    });
  }
  return (
    <form className = "mx-auto">
      <div class="mb-3">
        <label>
          Order ID
          <input
            type="text"
            value={state.order_id}
            onChange={handleChange}
          />
        </label>
      </div>
      <div class="mb-3">
        <label>
          Offer price
          <input
            type="text"
            value={state.offered_price}
            onChange={handleChange}
          />
        </label>
      </div>
      <div class="mb-3">
        <label>
          Suggest date
          <input
            type="text"
            value={state.delivery_date}
            onChange={handleChange}
          />
        </label>
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
    </form>
  );
}
