import React from 'react'
import MDBInput from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export function Offer () {
  const [order_id, setOrderId] = useState("");
  const [offered_Price, setPrice] = useState("");
  const [delivery_date, setDate] = useState("");

  const [state, setState] = React.useState({
    order_id: "",
    offered_price: "",
    delivery_date: ""
  })

  function handleChange(evt) {
    const value = evt.target.value;
    setState({
      ...state,
      [evt.target.name]: value
    });
  }


  return (
    <form onSubmit={handleSubmit} className = "mx-auto">
      <div class="mb-3">
        <label for="order_id" class="form-label">Order ID</label>
          <input
            type="text"
            defaultVdsalue={state.order_id}
            onChange={(e) => setOrderId(e.target.value)}
            class="form-control"
          />
      </div>
      <div class="mb-3">
        <label class="form-label">
          Offer price in cents
          <input
            type="text"
            defaultValue={state.offered_price}
            onChange={(e) => setPrice(e.target.value)}
            class="form-control"
          />
        </label>
      </div>
      <div class="mb-3">
        <label class="form-label">
          Suggest date
          <input
            type="datetime-local"
            defaultValue={state.delivery_date}
            onChange={(e) => setDate(e.target.value)}
            class="form-control"
          />
        </label>
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
    </form>
  );
}
