import React, { useEffect, useState } from 'react'
import axios from 'axios';

export const Order = () => {
    const [ord, getOrders] = useState('');

    //Loads data when  the page is loaded
    useEffect(()=> {
        getAllOrders();
    }, []);

    //sends a @GET request to API
    const getAllOrders = () => {
    axios.get('https://pasd-webshop-api.onrender.com/api/order/', {
        headers: {
            'accept': 'application/json',
            'x-api-key': '4sqmKzToVkUoTsVfze4X'
        }
    }).then(res => {
        const allOrders = res.data.orders;
        getOrders(allOrders);
    });
    }


    function Order(props) {
        //console.log(props.orders)
        const {menu, orders} = props;
        //console.log(orders.length);
        if(orders.length > 0) {
            return(
                orders.map((order, index) => {
                    console.log(order);
                    return (
                        <div key = {order.id}>
                            <h1>{order.id}</h1>
                            <h5>{order.send_date}</h5>
                            <h5>{order.x_in_mm}</h5>
                            <h5>{order.y_in_mm}</h5>
                            <h5>{order.receiver_info.name}</h5>
                            <h5>{order.sender_info.name}</h5>
                        </div>
                    )
                })
            )
        } else {
            return <h3>No orders yet.</h3>
        }
    }

    return (
       <Order orders = {ord} />
    )
}
